queries.aggrQuery:
{
  "aggs": {
    "AGGR": {
      "filters": {
        "filters": {
          "Campaign Filter": {
            "bool": {
              "must": [
                {
                  "term": {
                    "Data.targetType.keyword": "HOUSEHOLD"
                  }
                },
                {
                  "match_phrase": {
                    "Data.productVariant": "PVAR"
                  }
                },
                {
                  "exists": {
                    "field":"Data.province.keyword"
                  }
                }
              ],
              "must_not": [
                {
                  "exists": {
                    "field":"Data.district.keyword"
                  }
                }
              ]
            }
          }
        }
      },
      "aggs": {
        "Overall Target": {
          "sum": {
            "field": "Data.overallTarget"
          }
        },
        "Target per day": {
          "sum": {
            "field": "Data.targetPerDay"
          }
        },
        "Target Households per Day": {
          "bucket_script": {
            "buckets_path": {
              "tpd": "Target per day"
            },
            "script": "(params.tpd)*365"
          }
        }
      }
    }
  }
}

