{
  "aggs": {
    "FILTERS": {
      "filter": {
        "bool": {
          "must": [
            {
              "terms": {
                "Data.role.keyword": [
                  "DISTRIBUTOR",
                  "WAREHOUSE_MANAGER",
                  "LOCAL_MONITOR",
                  "NATIONAL_SUPERVISOR",
                  "DISTRICT_SUPERVISOR",
                  "PROVINCIAL_SUPERVISOR"
                ]
              }
            }
          ]
        }
      },
      "aggs": {
        "AGGS": {
          "terms": {
            "field": "Data.province.keyword",
            "size": 500
          },
          "aggs": {
            "Unique Users Synced": {
              "scripted_metric": {
                "params": {
                  "fieldName": "Data.userId.keyword"
                },
                "init_script": "state.list = []",
                "map_script": "if(doc[params.fieldName] != null) \nstate.list.add(doc[params.fieldName].value);",
                "combine_script": "return state.list;",
                "reduce_script": "Map uniqueValueMap = new HashMap(); \n        int count = 0;\n        for(shardList in states) {\n          if(shardList != null) { \n            for(key in shardList) {\n              if(!uniqueValueMap.containsKey(key)) {\n                count +=1;\n                uniqueValueMap.put(key, key);\n              }\n            }\n          }\n        } \n        return count;"
              }
            }
          }
        }
      }
    }
  }
}

